import React, { Component } from 'react';
import AlienClockDisplay from './alienClockDisplay'; // Update to match the actual filename
import SetAlienClockButton from './setAlienClockButton'; // Update to match the actual filename

class AlienClockApp extends Component {
  constructor(props) {
    super(props);

    // Define the number of days in an Alien month for two hypothetical alien months
    this.daysInMonth = [
      [44, 42, 48, 40, 48, 44, 40, 44, 42],
      [40, 40, 42, 44, 48, 42, 40, 44, 38],
    ];

    // Get the current Earth time
    const now = new Date();

    // Calculate the initial Alien time based on the current Earth time
    const alienTime = this.getAlienTime(now);

    this.state = {
      alienYear: alienTime.year,
      alienMonth: alienTime.month,
      alienDay: alienTime.day,
      alienHour: alienTime.hour,
      alienMinute: alienTime.minute,
      alienSecond: alienTime.second,
    };
  }

  componentDidMount() {
    // Set up an interval to update the Alien clock
    this.updateClockInterval = setInterval(this.updateAlienClock, 500); // Update every 1 alien second (every 500ms Earth second)
  }

  componentWillUnmount() {
    // Clear the interval when the component is unmounted
    clearInterval(this.updateClockInterval);
  }

  getAlienTime = (earthTime) => {
    // Calculate Alien time values based on the current Earth time
    const earthYear = earthTime.getFullYear();
    const earthMonth = earthTime.getMonth() + 1;
    const earthDay = earthTime.getDate();
    const earthHour = earthTime.getHours();
    const earthMinute = earthTime.getMinutes();
    const earthSecond = earthTime.getSeconds();

    // Implement your conversion logic to calculate Alien time here
    // For now, I'll provide a simple example of a hypothetical conversion:
    const alienYear = earthYear - 1000; // Hypothetical conversion
    const alienMonth = earthMonth + 6; // Hypothetical conversion
    const alienDay = earthDay; // Hypothetical conversion
    const alienHour = earthHour * 2; // Hypothetical conversion
    const alienMinute = earthMinute * 3; // Hypothetical conversion
    const alienSecond = earthSecond * 4; // Hypothetical conversion

    return {
      year: alienYear,
      month: alienMonth,
      day: alienDay,
      hour: alienHour,
      minute: alienMinute,
      second: alienSecond,
    };
  };

  updateAlienClock = () => {
    // Implement the logic to update the Alien time
    // This is a placeholder and should be replaced with your actual update logic
    const { alienYear, alienMonth, alienDay, alienHour, alienMinute, alienSecond } = this.state;

    // Hypothetical update logic: Increment the Alien time
    const updatedAlienSecond = alienSecond + 1;

    // Update the state with the new Alien time
    this.setState({ alienSecond: updatedAlienSecond });
  };

  setAlienClock = () => {
    // Implement the logic to set the Alien time based on user input
    // You can use this method to allow users to set a specific Alien time
  };

  render() {
    const { alienYear, alienMonth, alienDay, alienHour, alienMinute, alienSecond } = this.state;

    return (
      <div className="alien-clock">
        <AlienClockDisplay
          alienYear={alienYear}
          alienMonth={alienMonth}
          alienDay={alienDay}
          alienHour={alienHour}
          alienMinute={alienMinute}
          alienSecond={alienSecond}
        />
        <SetAlienClockButton onClick={this.setAlienClock} />
      </div>
    );
  }
}

export default AlienClockApp;
